import React, { forwardRef, useState } from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import Box from "./Box";
import Text from "./Text";
import Image from "./Image";

const emptyAvatar =
  "data:image/svg+xml;base64,";

const InitialsElement = styled(Box)`
  text-transform: uppercase;
  text-align: center;
  line-height: ${props => props.width + "px"};
`;

const Avatar = forwardRef(
  ({ src, alt, width, initials = "", effect, outerCircle, ...props }, ref) => {
    const [avatarWidth, setAvatarWidth] = useState(width);
    const [fontWeight, setFontWeight] = useState("normal");

    const onMouseEnter = e => {
      //console.log("ENTER ", effect);

      if (effect && effect.hover) {
        setAvatarWidth(effect.hover.width);
        setFontWeight(900);
      }
      //setAvatarWidth(42);
    };
    const onMouseLeave = e => {
      if (effect && effect.hover) {
        setAvatarWidth(width);
        setFontWeight("normal");
      }
    };

    if (src || initials === "") {
      return (
        <Image
          src={src || emptyAvatar}
          alt={alt}
          shape={"circle"}
          width={avatarWidth}
          onMouseEnter={onMouseEnter}
          onMouseLeave={onMouseLeave}
          ref={ref}
          {...props}
          {...(outerCircle
            ? { border: `${avatarWidth / 5}px solid #F5F8F7` }
            : {})}
        />
      );
    } else {
      const fontSize = width / (initials.length + 0.3);

      return (
        <InitialsElement
          borderWidth={1}
          borderStyle={"solid"}
          borderRadius={"50%"}
          fontSize={fontSize}
          fontWeight={fontWeight}
          width={avatarWidth}
          height={avatarWidth}
          colorStyle={"avatar"}
          onMouseEnter={onMouseEnter}
          onMouseLeave={onMouseLeave}
          ref={ref}
          {...props}
          {...(outerCircle
            ? { border: `${avatarWidth / 5}px solid #F5F8F7` }
            : {})}
        >
          <Text as={"span"} colorStyle={"avatar"}>
            {initials}
          </Text>
        </InitialsElement>
      );
    }
  },
);
Avatar.displayName = "Avatar";
Avatar.propTypes = {
  src: PropTypes.string,
  alt: PropTypes.string,
  initials: PropTypes.string,
  outerCircle: PropTypes.bool,
  width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]).isRequired,
};
export default Avatar;
